<?php session_start(); require_once '../../backend/config/db.php'; $error = ''; if ($_SERVER['REQUEST_METHOD'] === 'POST') { $email = $_POST['email']; $senha = $_POST['senha']; $stmt = $conn->prepare("SELECT * FROM usuarios WHERE email = :email"); $stmt->bindValue(':email', $email); $stmt->execute(); $usuario = $stmt->fetch(PDO::FETCH_ASSOC); if ($usuario && password_verify($senha, $usuario['senha'])) { $_SESSION['usuario_id'] = $usuario['id']; $_SESSION['usuario_nome'] = $usuario['nome']; header('Location: index.php'); exit; } else { $error = 'Email ou senha incorretos.'; } } ?> <?php include 'includes/header.php'; ?> <?php include 'includes/navbar.php'; ?> <main> <section class="login-section"> <h2>Entrar</h2> <form method="POST"> <label>Email:</label> <input type="email" name="email" required> <label>Senha:</label> <input type="password" name="senha" required> <button type="submit">Entrar</button> <p>Ainda nÃ£o tem conta? <a href="register.php">Cadastre-se</a></p> <?php if ($error): ?> <p class="error"><?= $error ?></p> <?php endif; ?> </form> </section> </main> <?php include 'includes/footer.php'; ?>