<?php require_once '../../backend/config/db.php'; $error = ''; $success = ''; if ($_SERVER['REQUEST_METHOD'] === 'POST') { $nome = $_POST['nome']; $email = $_POST['email']; $senha = password_hash($_POST['senha'], PASSWORD_DEFAULT); try { $stmt = $conn->prepare("INSERT INTO usuarios (nome, email, senha) VALUES (:nome, :email, :senha)"); $stmt->bindValue(':nome', $nome); $stmt->bindValue(':email', $email); $stmt->bindValue(':senha', $senha); $stmt->execute(); $success = "Conta criada com sucesso! FaÃ§a login."; } catch (PDOException $e) { $error = "Erro ao registrar: " . $e->getMessage(); } } ?> <?php include 'includes/header.php'; ?> <?php include 'includes/navbar.php'; ?> <main> <section class="register-section"> <h2>Criar Conta</h2> <form method="POST"> <label>Nome:</label> <input type="text" name="nome" required> <label>Email:</label> <input type="email" name="email" required> <label>Senha:</label> <input type="password" name="senha" required> <button type="submit">Cadastrar</button> <?php if ($error): ?><p class="error"><?= $error ?></p><?php endif; ?> <?php if ($success): ?><p class="success"><?= $success ?></p><?php endif; ?> </form> </section> </main> <?php include 'includes/footer.php'; ?>